pragma solidity >=0.8.0 <0.9.0;

import "./SimpleMarket.sol";

contract IntermediateEvents {
   
}

contract IntermediateMarket is SimpleMarket, IntermediateEvents {

    address USDT_addr = address(0x110a13FC3efE6A245B50102D2d79B3E76125Ae83);
    address COMP_addr = address(0xf76D4a441E4ba86A923ce32B89AFF89dBccAA075);

    function AuthorizeUSDT(uint amt) public returns(bool) {
        ERC20 USDT = ERC20(USDT_addr);
        return USDT.approve(address(this), amt);
    }

    function AuthorizeCOMP(uint amt) public returns(bool) {
        ERC20 COMP = ERC20(COMP_addr);
        return COMP.approve(address(this), amt);
    }

}
